function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))}));document.querySelectorAll(".menu__item").forEach((t=>{t.addEventListener("click",(function(){s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);let swiper=new Swiper(".popular__slider",{slidesPerView:1,spaceBetween:2,navigation:{nextEl:".popular__slider-next",prevEl:".popular__slider-prev"},breakpoints:{640:{slidesPerView:2,spaceBetween:2},768:{slidesPerView:4,spaceBetween:2},1024:{slidesPerView:5,spaceBetween:2}}});